mapper.update:
UPDATE job_diary_day_received

	state = 2,
	read_time = #{readTime}

WHERE id = #{id}

UPDATE job_diary_week_received

	state = 2,
	read_time = #{readTime}

WHERE id = #{id}

UPDATE job_diary_month_received

	state = 2,
	read_time = #{readTime}

WHERE id = #{id}

UPDATE job_diary_is_day
completed_job = #{completedJob}, incomplete_job = #{incompleteJob}, coordina_job = #{coordinaJob}, job_remark = #{jobRemark}, job_title = #{jobTitle}, state = #{state}, enclosure_info = #{enclosureInfo}
WHERE id = #{id}

UPDATE job_diary_is_week
completed_job = #{completedJob}, this_work_summary = #{thisWorkSummary}, next_work_plan = #{nextWorkPlan}, coordina_job = #{coordinaJob}, job_remark = #{jobRemark}, job_title = #{jobTitle}, state = #{state}, enclosure_info = #{enclosureInfo}
WHERE id = #{id}

UPDATE job_diary_is_month
completed_job = #{completedJob}, this_work_summary = #{thisWorkSummary}, next_work_plan = #{nextWorkPlan}, coordina_job = #{coordinaJob}, job_remark = #{jobRemark}, job_title = #{jobTitle}, state = #{state}, enclosure_info = #{enclosureInfo}
WHERE id = #{id}

UPDATE job_diary_day_received

    state = 2,
    read_time = #{readTime}

WHERE received_id = #{userId}
AND state = 1

UPDATE job_diary_week_received

    state = 2,
    read_time = #{readTime}

WHERE received_id = #{userId}
AND state = 1

UPDATE job_diary_month_received

    state = 2,
    read_time = #{readTime}

WHERE received_id = #{userId}
AND state = 1
