update.foreach:
UPDATE dw_question_logic
title = #{item.title}, cg_qu_item_id = #{item.cgQuItemId}, ge_le = #{item.geLe}, logic_type = #{item.logicType}, score_num = #{item.scoreNum}, sk_qu_id = #{item.skQuId},
WHERE id = #{item.id}

UPDATE dw_qu_score
order_by_id = #{item.orderById}, option_name = #{item.optionName},
WHERE id = #{item.id}

UPDATE dw_qu_orderby
order_by_id = #{item.orderById}, option_name = #{item.optionName},
WHERE id = #{item.id}

UPDATE dw_qu_radio
order_by_id = #{item.orderById}, option_name = #{item.optionName}, check_type = #{item.checkType}, is_note = #{item.isNote}, is_required_fill = #{item.isRequiredFill},
WHERE id = #{item.id}

UPDATE dw_qu_checkbox
order_by_id = #{item.orderById}, option_name = #{item.optionName}, check_type = #{item.checkType}, is_note = #{item.isNote}, is_required_fill = #{item.isRequiredFill},
WHERE id = #{item.id}

UPDATE dw_qu_multi_fillblank
order_by_id = #{item.orderById}, option_name = #{item.optionName},
WHERE id = #{item.id}

UPDATE dw_qu_chen_column
order_by_id = #{item.orderById}, option_name = #{item.optionName},
WHERE id = #{item.id}

UPDATE dw_qu_chen_row
order_by_id = #{item.orderById}, option_name = #{item.optionName},
WHERE id = #{item.id}

mapper.update:
UPDATE dw_question

	order_by_id = (order_by_id - 1),

WHERE order_by_id > #{orderById}
	AND belong_id = #{belongId}

UPDATE dw_survey_directory
survey_state = '1', real_start_time = #{startTime}, survey_qu_num = #{questionNum}, an_item_least_num = #{questionNum},
WHERE id = #{id}

mapper.select:
SELECT
	a.id
FROM
	dw_survey_answer a
WHERE a.ip_addr = #{ip}
AND a.survey_id = #{id}

mapper.update:
UPDATE dw_survey_directory
real_end_time = #{realEndTime}, survey_state = '2', end_type = '3',
WHERE id = #{id}

UPDATE dw_survey_directory
real_end_time = #{realEndTime}, survey_state = '2', end_type = '2',
WHERE id = #{id}
