mapper.select:
SELECT a.id, a.`name` proName, a.`desc` proDesc, (SELECT COUNT(*) FROM sm_project_page b WHERE b.sm_project_id = a.id) pageNum, (SELECT COUNT(*) FROM sm_project_page c, sm_project_page_mode d WHERE c.sm_project_id = a.id AND d.sm_project_page_id = c.id) modelNum, CONVERT(a.create_time, char) createTime FROM sm_project a WHERE a.create_id = #{createId}
AND a.name LIKE '%${proName}%'
ORDER BY a.create_time DESC

mapper.delete:
DELETE
FROM
	sm_project
WHERE
	id = #{id}
	AND create_id = #{createId}
